import{_ as r,c as t,e as a,o as i}from"./app-Dwsnbiv5.js";const n={};function o(p,e){return i(),t("div",null,e[0]||(e[0]=[a('<h1 id="历史杂记" tabindex="-1"><a class="header-anchor" href="#历史杂记"><span>历史杂记</span></a></h1><h1 id="目录" tabindex="-1"><a class="header-anchor" href="#目录"><span>目录</span></a></h1><h1 id="苏轼" tabindex="-1"><a class="header-anchor" href="#苏轼"><span>苏轼</span></a></h1><h2 id="仕途" tabindex="-1"><a class="header-anchor" href="#仕途"><span>仕途</span></a></h2><p>资料来源：维基</p><table><thead><tr><th>纪年</th><th>年份</th><th>主事</th><th>详情</th></tr></thead><tbody><tr><td>0000000</td><td>0000</td><td>0000000</td><td></td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E5%98%89%E4%BD%91" target="_blank" rel="noopener noreferrer">嘉佑</a>二年</td><td>1057</td><td>科举</td><td>苏轼才20岁，与弟弟<a href="https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BE%99" target="_blank" rel="noopener noreferrer">苏辙</a>一同进京参加会考，苏轼中进士第2名。<br>当时主试官是<a href="https://zh.wikipedia.org/wiki/%E6%AD%90%E9%99%BD%E8%84%A9" target="_blank" rel="noopener noreferrer">欧阳脩</a>，苏轼以一篇《刑赏忠厚之至论》的论文得到考官梅尧臣的青睐，并推荐给主试官欧阳修。欧阳修亦十分赞赏，原本欲拔擢为第一，但又怕该文为自己的门生<a href="https://zh.wikipedia.org/wiki/%E6%9B%BE%E9%9E%8F" target="_blank" rel="noopener noreferrer">曾巩</a>所作，为了避嫌，列为第二。结果试卷拆封后才发现该文为苏轼所作，而取为第一的是<a href="https://zh.wikipedia.org/wiki/%E6%9B%BE%E9%9E%8F" target="_blank" rel="noopener noreferrer">曾巩</a>，正是阴错阳差，弄巧成拙。<br>到了礼部复试时，苏轼再以《春秋对义》居曾巩之前，中乙科。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E6%B2%BB%E5%B9%B3" target="_blank" rel="noopener noreferrer">治平</a>三年</td><td>1066</td><td>父过世</td><td>父<a href="https://zh.wikipedia.org/wiki/%E8%98%87%E6%B4%B5" target="_blank" rel="noopener noreferrer">苏洵</a>过世，苏轼回<a href="https://zh.wikipedia.org/wiki/%E8%9C%80" target="_blank" rel="noopener noreferrer">蜀</a>守丧，英宗怜之，同意以官船载运苏轼一家。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E7%86%99%E5%AF%A7" target="_blank" rel="noopener noreferrer">熙宁</a>二年</td><td>1069</td><td></td><td>任祠部员外郎，反对<a href="https://zh.wikipedia.org/wiki/%E7%8E%8B%E5%AE%89%E7%9F%B3%E8%AE%8A%E6%B3%95" target="_blank" rel="noopener noreferrer">王安石变法</a>中的一些作为，<a href="https://zh.wikipedia.org/wiki/%E7%8E%8B%E5%AE%89%E7%9F%B3" target="_blank" rel="noopener noreferrer">王安石</a>于是屡次在神宗面前诋毁苏轼，<a href="https://zh.wikipedia.org/wiki/%E5%8F%B8%E9%A6%AC%E5%85%89" target="_blank" rel="noopener noreferrer">司马光</a>、<a href="https://zh.wikipedia.org/wiki/%E8%8C%83%E9%8E%AE" target="_blank" rel="noopener noreferrer">范镇</a>举荐苏轼作<a href="https://zh.wikipedia.org/wiki/%E8%AB%AB%E5%AE%98" target="_blank" rel="noopener noreferrer">谏官</a>，王安石力反之，神宗想让苏轼写<a href="https://zh.wikipedia.org/wiki/%E8%B5%B7%E5%B1%85%E6%B3%A8" target="_blank" rel="noopener noreferrer">起居注</a>，王安石向神宗进言，说苏轼在回家守丧时，乘机贩运<a href="https://zh.wikipedia.org/wiki/%E8%98%87%E6%9C%A8" target="_blank" rel="noopener noreferrer">苏木</a>（一种染料），最后神宗放弃这个任命。<br>三年，因为苏轼一直反对王安石，王安石门下的<a href="https://zh.wikipedia.org/wiki/%E5%BE%A1%E5%8F%B2" target="_blank" rel="noopener noreferrer">御史</a><a href="https://zh.wikipedia.org/wiki/%E8%AC%9D%E6%99%AF%E6%BA%AB" target="_blank" rel="noopener noreferrer">谢景温</a>又诬陷苏轼贩卖<a href="https://zh.wikipedia.org/wiki/%E7%A7%81%E7%9B%90" target="_blank" rel="noopener noreferrer">私盐</a>，<a href="https://zh.wikipedia.org/wiki/%E8%8C%83%E9%8E%AE" target="_blank" rel="noopener noreferrer">范镇</a>极辩苏轼贩盐之诬，并愿意退休负责。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E7%86%99%E5%AE%81" target="_blank" rel="noopener noreferrer">熙宁</a>三年</td><td>1070</td><td></td><td>苏轼担任当年度的科举主考官，苏轼本欲拟<a href="https://zh.wikipedia.org/wiki/%E4%B8%8A%E5%AE%98%E5%9D%87" target="_blank" rel="noopener noreferrer">上官均</a>为第一名（状元），因发现上官均的策论有诋毁王安石变法的情况，便改上官均为第二名。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E7%86%99%E5%AE%81" target="_blank" rel="noopener noreferrer">熙宁</a>五年</td><td>1072</td><td></td><td>苏轼因不堪<a href="https://zh.wikipedia.org/wiki/%E6%96%B0%E9%BB%A8" target="_blank" rel="noopener noreferrer">新党</a>的迫害，求外职，神宗本欲予以<a href="https://zh.wikipedia.org/wiki/%E7%9F%A5%E5%B7%9E" target="_blank" rel="noopener noreferrer">知州</a>，但王安石只愿予之<a href="https://zh.wikipedia.org/wiki/%E9%A2%8D%E5%B7%9E" target="_blank" rel="noopener noreferrer">颍州</a><a href="https://zh.wikipedia.org/wiki/%E9%80%9A%E5%88%A4" target="_blank" rel="noopener noreferrer">通判</a>，神宗最后折中，让苏轼担任比较好的<a href="https://zh.wikipedia.org/wiki/%E6%9D%AD%E5%B7%9E" target="_blank" rel="noopener noreferrer">杭州</a><a href="https://zh.wikipedia.org/wiki/%E9%80%9A%E5%88%A4" target="_blank" rel="noopener noreferrer">通判</a>，三年之后升为<a href="https://zh.wikipedia.org/wiki/%E7%9F%A5%E5%B7%9E" target="_blank" rel="noopener noreferrer">知州</a>，连知<a href="https://zh.wikipedia.org/wiki/%E5%AF%86%E5%B7%9E" target="_blank" rel="noopener noreferrer">密州</a>、<a href="https://zh.wikipedia.org/wiki/%E5%BE%90%E5%B7%9E" target="_blank" rel="noopener noreferrer">徐州</a>、<a href="https://zh.wikipedia.org/wiki/%E6%B9%96%E5%B7%9E" target="_blank" rel="noopener noreferrer">湖州</a>。<br>熙宁十年四月，赴任徐州，是年七月七日，<a href="https://zh.wikipedia.org/wiki/%E9%BB%84%E6%B2%B3" target="_blank" rel="noopener noreferrer">黄河</a>决口，水困<a href="https://zh.wikipedia.org/wiki/%E5%BE%90%E5%B7%9E" target="_blank" rel="noopener noreferrer">徐州</a>，苏轼参加救灾。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E5%85%83%E4%B8%B0_(%E5%8C%97%E5%AE%8B)" target="_blank" rel="noopener noreferrer">元丰</a>二年</td><td>1079</td><td>乌台诗案<br>被贬黄州</td><td>四十三岁时，因<a href="https://zh.wikipedia.org/wiki/%E7%83%8F%E8%87%BA%E8%A9%A9%E6%A1%88" target="_blank" rel="noopener noreferrer">乌台诗案</a>入狱，几死，因为写文章向<a href="https://zh.wikipedia.org/wiki/%E6%9C%9D%E5%BB%B7" target="_blank" rel="noopener noreferrer">朝廷</a>诀别，<a href="https://zh.wikipedia.org/wiki/%E6%85%88%E8%81%96%E7%9A%87%E5%90%8E" target="_blank" rel="noopener noreferrer">太皇太后曹氏</a>、<a href="https://zh.wikipedia.org/wiki/%E7%8E%8B%E5%AE%89%E7%A4%BC" target="_blank" rel="noopener noreferrer">王安礼</a>等人出面力挽，神宗动心，苏轼终免一死，贬谪为“检校尚书水部员外郎黄州团练副使本州安置”，为其文学创作生涯的重要阶段，而神宗亦爱其才，终得以保全，翌年被贬至<a href="https://zh.wikipedia.org/wiki/%E9%BB%84%E5%B7%9E" target="_blank" rel="noopener noreferrer">黄州</a>（今湖北省黄冈市），在黄州“深自闭塞，扁舟革履，放浪山水之间，与渔樵杂处”[<a href="https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BD%BC#cite_note-12" target="_blank" rel="noopener noreferrer">12]</a>，与<a href="https://zh.wikipedia.org/wiki/%E5%BC%B5%E6%87%B7%E6%B0%91" target="_blank" rel="noopener noreferrer">张怀民</a>交游，也结交<a href="https://zh.wikipedia.org/wiki/%E7%A6%AA%E9%96%80" target="_blank" rel="noopener noreferrer">禅门</a>人士，当时<a href="https://zh.wikipedia.org/wiki/%E4%BD%9B%E5%8D%B0_(%E5%AE%8B%E6%9C%9D)" target="_blank" rel="noopener noreferrer">佛印</a>担任<a href="https://zh.wikipedia.org/wiki/%E5%BA%90%E5%B1%B1" target="_blank" rel="noopener noreferrer">庐山</a><a href="https://zh.wikipedia.org/w/index.php?title=%E5%BD%92%E5%AE%97%E5%AF%BA&amp;action=edit&amp;redlink=1" target="_blank" rel="noopener noreferrer">归宗寺</a>住持，与苏轼时有往来[<a href="https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BD%BC#cite_note-13" target="_blank" rel="noopener noreferrer">13]</a>。苏轼有〈戏答佛印偈〉曰：“百千灯作一灯光，尽是<a href="https://zh.wikipedia.org/wiki/%E6%81%86%E6%B2%B3%E6%B2%99" target="_blank" rel="noopener noreferrer">恒沙</a><a href="https://zh.wikipedia.org/wiki/%E4%BD%9B" target="_blank" rel="noopener noreferrer">妙法王</a>，是故东坡不敢借，借君四大作禅床。”元丰七年离开黄州。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E5%85%83%E7%A5%90" target="_blank" rel="noopener noreferrer">元祐</a>元年</td><td>1086</td><td></td><td><a href="https://zh.wikipedia.org/wiki/%E5%AE%8B%E5%93%B2%E5%AE%97" target="_blank" rel="noopener noreferrer">宋哲宗</a>即位，<a href="https://zh.wikipedia.org/wiki/%E5%AE%A3%E4%BB%81%E7%9A%87%E5%90%8E" target="_blank" rel="noopener noreferrer">高太皇太后</a>垂帘听政，回朝任<a href="https://zh.wikipedia.org/wiki/%E7%A4%BC%E9%83%A8" target="_blank" rel="noopener noreferrer">礼部</a><a href="https://zh.wikipedia.org/wiki/%E9%83%8E%E4%B8%AD" target="_blank" rel="noopener noreferrer">郎中</a>、<a href="https://zh.wikipedia.org/wiki/%E4%B8%AD%E4%B9%A6%E8%88%8D%E4%BA%BA" target="_blank" rel="noopener noreferrer">中书舍人</a>、翰林学士，元祐四年（1089年）拜<a href="https://zh.wikipedia.org/wiki/%E9%BE%99%E5%9B%BE%E9%98%81" target="_blank" rel="noopener noreferrer">龙图阁</a>学士，曾出知<a href="https://zh.wikipedia.org/wiki/%E6%9D%AD%E5%B7%9E" target="_blank" rel="noopener noreferrer">杭州</a>、<a href="https://zh.wikipedia.org/wiki/%E9%A2%8D%E5%B7%9E" target="_blank" rel="noopener noreferrer">颍州</a>等，官至<a href="https://zh.wikipedia.org/wiki/%E7%A6%AE%E9%83%A8%E5%B0%9A%E6%9B%B8" target="_blank" rel="noopener noreferrer">礼部尚书</a>。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E7%BB%8D%E5%9C%A3" target="_blank" rel="noopener noreferrer">绍圣</a>元年</td><td>1094</td><td>被贬惠州、儋州</td><td>被哲宗贬谪至<a href="https://zh.wikipedia.org/wiki/%E6%83%A0%E5%B7%9E" target="_blank" rel="noopener noreferrer">惠州</a>、<a href="https://zh.wikipedia.org/wiki/%E5%84%8B%E5%B7%9E" target="_blank" rel="noopener noreferrer">儋州</a>（<a href="https://zh.wikipedia.org/wiki/%E6%B5%B7%E5%8D%97%E5%B3%B6" target="_blank" rel="noopener noreferrer">海南岛</a>）。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E5%85%83%E7%AC%A6" target="_blank" rel="noopener noreferrer">元符</a>三年</td><td>1100</td><td>北还</td><td><a href="https://zh.wikipedia.org/wiki/%E5%AE%8B%E5%BE%BD%E5%AE%97" target="_blank" rel="noopener noreferrer">宋徽宗</a>即位，<a href="https://zh.wikipedia.org/wiki/%E6%AC%BD%E8%81%96%E7%9A%87%E5%90%8E" target="_blank" rel="noopener noreferrer">向太后</a>垂帘听政，下诏让苏轼北还。</td></tr><tr><td><a href="https://zh.wikipedia.org/wiki/%E5%BB%BA%E4%B8%AD%E9%9D%96%E5%9B%BD" target="_blank" rel="noopener noreferrer">建中靖国</a>元年</td><td>1101</td><td></td><td>夏天因冷饮过度，<a href="https://zh.wikipedia.org/wiki/%E4%B8%8B%E7%97%A2" target="_blank" rel="noopener noreferrer">下痢</a>不止，又误服<a href="https://zh.wikipedia.org/wiki/%E9%BB%84%E8%8A%AA" target="_blank" rel="noopener noreferrer">黄芪</a>，结果病情恶化，“齿间出血如蚯蚓者无数”，七月二十八日于<a href="https://zh.wikipedia.org/wiki/%E5%B8%B8%E5%B7%9E" target="_blank" rel="noopener noreferrer">常州</a><a href="https://zh.wikipedia.org/wiki/%E8%97%A4%E8%8A%B1%E6%97%A7%E9%A6%86" target="_blank" rel="noopener noreferrer">孙氏馆</a>病卒，[<a href="https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BD%BC#cite_note-14" target="_blank" rel="noopener noreferrer">14]</a>享年六十四岁。由弟苏辙归葬于郏县小峨眉山。<a href="https://zh.wikipedia.org/wiki/%E5%8D%97%E5%AE%8B" target="_blank" rel="noopener noreferrer">南宋</a><a href="https://zh.wikipedia.org/wiki/%E5%AE%8B%E5%AD%9D%E5%AE%97" target="_blank" rel="noopener noreferrer">宋孝宗</a><a href="https://zh.wikipedia.org/wiki/%E8%BF%BD%E8%B4%88" target="_blank" rel="noopener noreferrer">追赠</a>谥号“文忠”。</td></tr></tbody></table><p>苏轼疲于应付<a href="https://zh.wikipedia.org/wiki/%E6%96%B0%E8%88%8A%E9%BB%A8%E7%88%AD" target="_blank" rel="noopener noreferrer">新旧党争</a>，遇事“如食内有蝇，吐之乃已”[<a href="https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BD%BC#cite_note-15" target="_blank" rel="noopener noreferrer">15]</a>，苏轼既反对<a href="https://zh.wikipedia.org/wiki/%E7%8E%8B%E5%AE%89%E7%9F%B3" target="_blank" rel="noopener noreferrer">王安石</a>比较急进的<a href="https://zh.wikipedia.org/wiki/%E7%86%99%E5%AF%A7%E8%AE%8A%E6%B3%95" target="_blank" rel="noopener noreferrer">改革措施</a>，也不同意旧党<a href="https://zh.wikipedia.org/wiki/%E5%8F%B8%E9%A9%AC%E5%85%89" target="_blank" rel="noopener noreferrer">司马光</a><a href="https://zh.wikipedia.org/wiki/%E5%85%83%E7%A5%90%E6%9B%B4%E5%8C%96" target="_blank" rel="noopener noreferrer">尽废新法</a>[<a href="https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BD%BC#cite_note-16" target="_blank" rel="noopener noreferrer">16]</a>，所以虽然新党一直称苏轼为旧党，但其实他在新旧两党之间均受排斥，仕途坎坷，时常远贬外方，不过他在各地居官清正，为民兴利除弊，政绩颇善，口碑甚佳，杭州<a href="https://zh.wikipedia.org/wiki/%E8%A5%BF%E6%B9%96" target="_blank" rel="noopener noreferrer">西湖</a>的<a href="https://zh.wikipedia.org/wiki/%E8%8B%8F%E5%A0%A4" target="_blank" rel="noopener noreferrer">苏堤</a>就是实证。</p><h2 id="其他" tabindex="-1"><a class="header-anchor" href="#其他"><span>其他</span></a></h2><h3 id="在下东坡-一个吃货" tabindex="-1"><a class="header-anchor" href="#在下东坡-一个吃货"><span>在下东坡，一个吃货</span></a></h3><h2 id="作品" tabindex="-1"><a class="header-anchor" href="#作品"><span>作品</span></a></h2><h3 id="《水调歌头》" tabindex="-1"><a class="header-anchor" href="#《水调歌头》"><span>《水调歌头》</span></a></h3><p>乌台诗案前，被贬前，熙宁九年作</p><h3 id="《念奴娇・赤壁怀古》" tabindex="-1"><a class="header-anchor" href="#《念奴娇・赤壁怀古》"><span>《念奴娇・赤壁怀古》</span></a></h3><p>被贬黄州后作，元丰二年被贬，元丰五年作</p><p>诗词故事</p><p>原作：https://www.jianshu.com/p/32758f01ac1e</p><blockquote><p>北宋神宗元丰二年，经历了<code>乌台诗案</code>的苏东坡被贬官到黄州做了团练副使，上面还不准他签署公事。政治上的不得志，生活上的颠沛流离，使这位曾经有着远大抱负，想干一番事业的诗人一下子变得悲观失望起来。</p><p>元丰五年七月，在黄州任上，已经四十七岁的苏东坡，怀着忧郁矛盾的心情游览了赤壁。</p><p>这是农历七月一天的黄昏，苏东坡独自来到黄州城外，乘着渔家的小船，漂泊到赤壁山下。他伫立船头，放眼暸望赤壁矶一带雄奇壮丽的风光，心中涌起了无限的激情。看着眼前奔腾东去的长江，他禁不住回想起了古代的许多英雄豪杰，感叹他们一个个都像这流水一样一去不复返了。这些英雄人物当中，最使他难忘的是三国时代的周瑜。这位才智出众的青年将领一直是东坡崇拜的典范此刻，在这赤壁圣地，周郎的形象又一次浮现在他的脑海中。</p><p>虽然苏东坡知道，这黄州的<strong>赤壁矶并非是当年三国赤壁大战之地</strong>，但相同的山名、相似的风景仍然让他浮想联翩。他不由把这赤壁矶当做当年的赤壁鏖战之地来欣赏了。</p><p>东坡乘着小船环游赤壁，尽情地欣赏着四周景色；他一会昂着眺望赤壁山峰，只见乱石迭宕，犹如犬牙交错，直刺青天；一会儿俯首鸟瞰山下水势，又见汹涌的波涛拍打着江岸，山水之间浪花飞溅，就好像平地卷起的一堆堆白雪一样。这如描如绘的秀丽山川简直把东坡给迷住了，他不禁失声赞叹：</p><p>“何等壮周的景致呀！这不就是当年周郎一代英雄例功立业的地方吗？”</p><p>于是，苏东坡再也控制不住激荡的思潮，久久地陷入了神驰遐想之中。他追忆起周郎在魏赤壁大显身手的情景：那还是</p><p>小乔(周瑜的妻子)刚刚出嫁的时候，三十岁的周郎就已经做了东吴军队的统帅。那时节，他年轻气盛，真是盖世英才啊！就在曹操亲率八十万大军进攻东吴之际，他手执羽扇，头戴纶巾，指挥将士，从容对敌，竟在谈笑之间大败曹军。</p><p>思念当年英雄，他不觉又联想到自身的处境：自己已经到了暮年时期，不但功名不能成就，反而屡屡遭受打击；两鬓已过早地斑白了，可报国的希望依旧那么渺茫。对照之下，东坡心中十分惭愧。他拈着胡须喟然长叹道：</p><p>“唉，人生真是一场梦啊！公瑾如果有知，一定会笑我自作多情了。”</p><p>这时，明月已经升起来了，周围的山水树木顿时笼置在朦胧的月色中。夜风徐徐吹过，江上涛声不断。东坡微微感觉到了一丝凉意，回首问船家：“船上有酒肴吗？”</p><p>船夫回答说：“备有酒菜，平日专供过往客人饮用。”</p><p>东坡便唤船家取过酒来，开始自掛自酌起来。他边饮边观赏这月色，但见浩翰的空中一弯新月倒挂，月光清淡，江水漓漓，使天地间含混一色。看着，看着，一股凄切寒怆之感不禁涌上他的心头；四海之大，天地之广，可有谁能真正了解我苏东坡呢？恐怕只有这江水和明月オ算是知已吧！想到这里，他向船家要来纸笔，写下了《赤壁怀古》这一豪放词篇：</p></blockquote><p>原词</p><blockquote><p>大江东去，浪淘尽，千古风流人物。故垒西边，人道是，三国周郎赤壁。乱石穿空，惊涛拍岸，卷起千堆雪。江山如画，一时多少豪杰。</p><p>遥想公瑾当年，小乔初嫁了，雄姿英发。羽扇纶巾，谈笑间，樯橹灰飞烟灭。故国神游，多情应笑我，早生华发。人生如梦，一尊还酹江月。</p><p>——北宋・苏轼《念奴娇・赤壁怀古》</p></blockquote><p>解析</p><blockquote><p>长江之水滚滚东流，大浪淘尽了千古风流人物。 旧时营垒的西边，人们都说这里就是三国时周瑜大胜曹军的古战场赤壁。</p><p>陡峭不平的石壁插人天空，惊人的巨浪拍打着两岸，卷起了无数的浪花。 江山美丽如画，每个时代都产生了无数豪杰。</p><p>遥想周瑜那个时侯，美丽的小乔刚刚嫁给了周瑜，周瑜正是年轻有为、英气勃发之时。 赤壁大战时，他一身儒将的装束，从容闲雅。他在谈笑之间，设下火攻之计，把敌人强大的船舰全部纵火烧毁。</p><p>现在，我神游于三国时代的古战场，应该笑自己的多情善感，头发都变成花白了。 人生就像做梦一样，我只能把酒酒在江中，对着明月来祭奠我心目中的英雄了。</p></blockquote><h3 id="自画像提诗句" tabindex="-1"><a class="header-anchor" href="#自画像提诗句"><span>自画像提诗句</span></a></h3><p>从儋州还时所写</p><blockquote><p>心似已灰之木，身如不系之舟，问汝平生功业，黄州惠州儋州。</p></blockquote><h1 id="溥仪" tabindex="-1"><a class="header-anchor" href="#溥仪"><span>溥仪</span></a></h1><p>[^溥仪]:溥pu3，逊帝全名：爱新觉罗 - 溥仪</p><h2 id="其他-1" tabindex="-1"><a class="header-anchor" href="#其他-1"><span>其他</span></a></h2><h3 id="我在我家偷文物" tabindex="-1"><a class="header-anchor" href="#我在我家偷文物"><span>我在我家偷文物</span></a></h3><p>庄士敦的英国人</p><h1 id="隋文帝、隋炀帝" tabindex="-1"><a class="header-anchor" href="#隋文帝、隋炀帝"><span>隋文帝、隋炀帝</span></a></h1><h2 id="其他-2" tabindex="-1"><a class="header-anchor" href="#其他-2"><span>其他</span></a></h2><h3 id="请回答604" tabindex="-1"><a class="header-anchor" href="#请回答604"><span>请回答604</span></a></h3><p>隋文帝扬坚：扬坚邂逅宫女，但被正宫独孤伽罗“特别关照”，扬坚便离家出走，成为第一个离家出走的黄帝。</p><p>隋炀帝杨广：心机重。次子杨广登基，隋炀帝。长子杨勇自缢身亡。做事荒唐使得民不聊生，引发农民起义，后遭遇宇文化及逼宫兵变，缢死。</p><p>隋炀帝墓的发现：工地发现，工地负责人居然叫杨勇！</p><h1 id="鼎" tabindex="-1"><a class="header-anchor" href="#鼎"><span>鼎</span></a></h1><h2 id="其他-3" tabindex="-1"><a class="header-anchor" href="#其他-3"><span>其他</span></a></h2><h3 id="一口锅的逆袭" tabindex="-1"><a class="header-anchor" href="#一口锅的逆袭"><span>一口锅的逆袭</span></a></h3><p>鼎</p><blockquote><p>鼎，原来是古老的食器，类似于现代的锅。</p><p>大禹将天下重新规划为九州。用青铜铸造象征王权的九鼎，将名山大川、奇珍异物，镌刻与九鼎之身，以一鼎象征一州。</p><p>从此，九鼎逐渐变成立国重器，传国之宝，甚至天命所归。</p></blockquote><p>问鼎的楚庄王</p><blockquote><p>楚庄王问周天子九鼎的事，即“问鼎”</p><p>楚庄王：</p><blockquote><p>荒废了三年 伍举貌似进谏，问楚庄王：南方有只大鸟，三年不飞不叫，请问这是什么鸟 楚庄王：不鸣则已，一鸣惊人，不飞则已，一飞冲天</p><p>虽无非，飞必冲天；虽无鸣，鸣必惊人。——《韩非子 · 喻老》</p></blockquote></blockquote><p>举鼎的秦武王</p><blockquote><p>秦武王嬴荡</p><p>自持大力，举鼎暴毙</p><p>“王与孟说举鼎，绝膑。八月，武王死”——《史记 · 秦本纪》</p><p>嬴荡去世后，弟弟嬴稷继位为秦昭襄王</p></blockquote><p>嬴稷</p><blockquote><p>未统一六国之前，消灭周王室，得到九鼎</p><p>但九鼎在运往秦都咸阳途中不幸沉入海底，就此再无音讯</p></blockquote><h1 id="何晏" tabindex="-1"><a class="header-anchor" href="#何晏"><span>何晏</span></a></h1><h2 id="其他-4" tabindex="-1"><a class="header-anchor" href="#其他-4"><span>其他</span></a></h2><h3 id="魏晋奇葩-傅粉何郎" tabindex="-1"><a class="header-anchor" href="#魏晋奇葩-傅粉何郎"><span>魏晋奇葩-傅粉何郎</span></a></h3><p>何晏：皮肤白、女装大佬、推广清谈，推广五石散，嗑药的祖先</p><p>身份：曹操的继子、养子、女婿三重身份</p><p>事件：</p><blockquote><p>曹丕当面叫“假子” 魏明帝曹叡夏天面片儿汤</p></blockquote><h1 id="嵇康" tabindex="-1"><a class="header-anchor" href="#嵇康"><span>嵇康</span></a></h1><h2 id="其他-5" tabindex="-1"><a class="header-anchor" href="#其他-5"><span>其他</span></a></h2><h3 id="魏晋奇葩-爱豆嵇康" tabindex="-1"><a class="header-anchor" href="#魏晋奇葩-爱豆嵇康"><span>魏晋奇葩-爱豆嵇康</span></a></h3><p>嵇康：会抚琴</p><p>身份：竹林七贤之精神领袖（竹林七贤之首是组织者，最年长的山涛）</p><p>事件：钟会仍《四本论》</p><blockquote><p>钟会完成新作《四本论》，希望嵇康给自己稍作点评</p><p>钟会撰《四本论》始毕，甚欲使嵇公一见，置怀中，既定，畏其难，怀不敢出，于户外遥掷，便回急走。——南朝宋 · 刘义庆《世说新语 · 文学》</p></blockquote><p>事件：被误当神仙下凡</p><blockquote><p>康尝采药游山泽，会其得意，忽焉忘反。时有樵苏者遇之，咸谓为神。——《晋书 · 嵇康传》</p></blockquote><p>事件：不当官</p><blockquote><p>山涛将去选官，举康自代。康乃与涛书告绝，作《与山巨源绝交书》。——《晋书 · 嵇康传》</p></blockquote><p>事件：</p><blockquote><p>初，康居贫，尝与向秀，共锻于大树之下，以自赡给。 颍川钟会，贵公子也，精练有才辩，故往造焉。康不为之礼，而锻不辍。 良久会去，康谓曰：“何所闻而来？何所见而去？” 会曰：“闻所闻而来，见所见而去。”会以此憾之。——《晋书 · 嵇康传》</p></blockquote><p>形貌：</p><blockquote><p>身长七尺八寸，美词气，有风仪，而土木形骸，不自藻饰。人以为龙章凤姿，天质自然。——《晋书》</p><p>嵇康，有奇才，远迈不群《晋书 · 嵇康传》</p></blockquote><h1 id="乾隆" tabindex="-1"><a class="header-anchor" href="#乾隆"><span>乾隆</span></a></h1><h2 id="其他-6" tabindex="-1"><a class="header-anchor" href="#其他-6"><span>其他</span></a></h2><h3 id="爱发弹幕的乾隆同学" tabindex="-1"><a class="header-anchor" href="#爱发弹幕的乾隆同学"><span>爱发弹幕的乾隆同学</span></a></h3><p>乾隆：大清第六代黄帝，爱新觉罗 · 弘历</p><p>题跋：</p><blockquote><p>宋代以后，书画的原作者和收藏者，往往会把他们对作品的评价，提写在画面上。逐渐形成了“题跋”的传统</p><p>好的题跋：不侵画位，不伤画局，有助于后人鉴别和欣赏。槽糕的题跋：却会破坏作品本身的格调和美感。</p></blockquote><h1 id="古代武器" tabindex="-1"><a class="header-anchor" href="#古代武器"><span>古代武器</span></a></h1><h2 id="弓箭" tabindex="-1"><a class="header-anchor" href="#弓箭"><span>弓箭</span></a></h2><p>养由基：</p><p>百发百中，百步穿杨</p><p>李广：</p><p>林暗草惊风，将军夜引弓。平明寻白羽，没在石棱中。——唐朝卢纶</p><h2 id="弩" tabindex="-1"><a class="header-anchor" href="#弩"><span>弩</span></a></h2><h2 id="刀" tabindex="-1"><a class="header-anchor" href="#刀"><span>刀</span></a></h2><h1 id="其他-7" tabindex="-1"><a class="header-anchor" href="#其他-7"><span>其他</span></a></h1><p>人生没有目的，只有过程，所谓的终极目的是虚无的。——尼采《查拉图斯特拉如是说》</p><p>在孤独中，孤独者将自己吃得一干二净，而在群体中，他被众人吃掉。——尼采《人性的，太人性的》</p><p>不论多么天衣无缝的犯罪，只要是人做的，就没有解不开的道理。——阿瑟 · 柯南 · 道尔《福尔摩斯全集》</p><p>时间是审查一切犯罪的最老练的法官。——莎士比亚</p><p>除去不可能的剩下的即使再不可能，那也是真相。——阿瑟 · 柯南 · 道尔《福尔摩斯》</p><p>人生而自由，这种自由是选择的自由。——萨特</p><p>名誉是一件无聊的骗人的东西！得到它的人未必有什么功德，失去它的人也未必有什么过失。——莎士比亚《奥赛罗》</p><p>不速之客只有在告辞以后才最受欢迎。——莎士比亚</p>',91)]))}const l=r(n,[["render",o],["__file","历史杂记.html.vue"]]),k=JSON.parse('{"path":"/MdNote_Public/02.%20SubjectAndKnowledge/06.%20%E5%8E%86%E5%8F%B2%E5%AD%A6/%E5%8E%86%E5%8F%B2/%E5%8E%86%E5%8F%B2%E6%9D%82%E8%AE%B0.html","title":"历史杂记","lang":"zh-CN","frontmatter":{"description":"历史杂记 目录 苏轼 仕途 资料来源：维基 苏轼疲于应付新旧党争，遇事“如食内有蝇，吐之乃已”[15]，苏轼既反对王安石比较急进的改革措施，也不同意旧党司马光尽废新法[16]，所以虽然新党一直称苏轼为旧党，但其实他在新旧两党之间均受排斥，仕途坎坷，时常远贬外方，不过他在各地居官清正，为民兴利除弊，政绩颇善，口碑甚佳，杭州西湖的苏堤就是实证。 其他 在下...","head":[["meta",{"property":"og:url","content":"https://LincZero.github.io/MdNote_Public/02.%20SubjectAndKnowledge/06.%20%E5%8E%86%E5%8F%B2%E5%AD%A6/%E5%8E%86%E5%8F%B2/%E5%8E%86%E5%8F%B2%E6%9D%82%E8%AE%B0.html"}],["meta",{"property":"og:site_name","content":"Linc 的小站"}],["meta",{"property":"og:title","content":"历史杂记"}],["meta",{"property":"og:description","content":"历史杂记 目录 苏轼 仕途 资料来源：维基 苏轼疲于应付新旧党争，遇事“如食内有蝇，吐之乃已”[15]，苏轼既反对王安石比较急进的改革措施，也不同意旧党司马光尽废新法[16]，所以虽然新党一直称苏轼为旧党，但其实他在新旧两党之间均受排斥，仕途坎坷，时常远贬外方，不过他在各地居官清正，为民兴利除弊，政绩颇善，口碑甚佳，杭州西湖的苏堤就是实证。 其他 在下..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"历史杂记\\",\\"image\\":[\\"\\"],\\"dateModified\\":null,\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"LincZero\\",\\"url\\":\\"https://github.com/LincZero/\\"}]}"]]},"headers":[{"level":1,"title":"历史杂记","slug":"历史杂记","link":"#历史杂记","children":[]},{"level":1,"title":"目录","slug":"目录","link":"#目录","children":[]},{"level":1,"title":"苏轼","slug":"苏轼","link":"#苏轼","children":[{"level":2,"title":"仕途","slug":"仕途","link":"#仕途","children":[]},{"level":2,"title":"其他","slug":"其他","link":"#其他","children":[{"level":3,"title":"在下东坡，一个吃货","slug":"在下东坡-一个吃货","link":"#在下东坡-一个吃货","children":[]}]},{"level":2,"title":"作品","slug":"作品","link":"#作品","children":[{"level":3,"title":"《水调歌头》","slug":"《水调歌头》","link":"#《水调歌头》","children":[]},{"level":3,"title":"《念奴娇・赤壁怀古》","slug":"《念奴娇・赤壁怀古》","link":"#《念奴娇・赤壁怀古》","children":[]},{"level":3,"title":"自画像提诗句","slug":"自画像提诗句","link":"#自画像提诗句","children":[]}]}]},{"level":1,"title":"溥仪","slug":"溥仪","link":"#溥仪","children":[{"level":2,"title":"其他","slug":"其他-1","link":"#其他-1","children":[{"level":3,"title":"我在我家偷文物","slug":"我在我家偷文物","link":"#我在我家偷文物","children":[]}]}]},{"level":1,"title":"隋文帝、隋炀帝","slug":"隋文帝、隋炀帝","link":"#隋文帝、隋炀帝","children":[{"level":2,"title":"其他","slug":"其他-2","link":"#其他-2","children":[{"level":3,"title":"请回答604","slug":"请回答604","link":"#请回答604","children":[]}]}]},{"level":1,"title":"鼎","slug":"鼎","link":"#鼎","children":[{"level":2,"title":"其他","slug":"其他-3","link":"#其他-3","children":[{"level":3,"title":"一口锅的逆袭","slug":"一口锅的逆袭","link":"#一口锅的逆袭","children":[]}]}]},{"level":1,"title":"何晏","slug":"何晏","link":"#何晏","children":[{"level":2,"title":"其他","slug":"其他-4","link":"#其他-4","children":[{"level":3,"title":"魏晋奇葩-傅粉何郎","slug":"魏晋奇葩-傅粉何郎","link":"#魏晋奇葩-傅粉何郎","children":[]}]}]},{"level":1,"title":"嵇康","slug":"嵇康","link":"#嵇康","children":[{"level":2,"title":"其他","slug":"其他-5","link":"#其他-5","children":[{"level":3,"title":"魏晋奇葩-爱豆嵇康","slug":"魏晋奇葩-爱豆嵇康","link":"#魏晋奇葩-爱豆嵇康","children":[]}]}]},{"level":1,"title":"乾隆","slug":"乾隆","link":"#乾隆","children":[{"level":2,"title":"其他","slug":"其他-6","link":"#其他-6","children":[{"level":3,"title":"爱发弹幕的乾隆同学","slug":"爱发弹幕的乾隆同学","link":"#爱发弹幕的乾隆同学","children":[]}]}]},{"level":1,"title":"古代武器","slug":"古代武器","link":"#古代武器","children":[{"level":2,"title":"弓箭","slug":"弓箭","link":"#弓箭","children":[]},{"level":2,"title":"弩","slug":"弩","link":"#弩","children":[]},{"level":2,"title":"刀","slug":"刀","link":"#刀","children":[]}]},{"level":1,"title":"其他","slug":"其他-7","link":"#其他-7","children":[]}],"git":{},"readingTime":{"minutes":14.07,"words":4221},"filePathRelative":"MdNote_Public/02. SubjectAndKnowledge/06. 历史学/历史/历史杂记.md","excerpt":"\\n<h1>目录</h1>\\n<h1>苏轼</h1>\\n<h2>仕途</h2>\\n<p>资料来源：维基</p>\\n<table>\\n<thead>\\n<tr>\\n<th>纪年</th>\\n<th>年份</th>\\n<th>主事</th>\\n<th>详情</th>\\n</tr>\\n</thead>\\n<tbody>\\n<tr>\\n<td>0000000</td>\\n<td>0000</td>\\n<td>0000000</td>\\n<td></td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E5%98%89%E4%BD%91\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">嘉佑</a>二年</td>\\n<td>1057</td>\\n<td>科举</td>\\n<td>苏轼才20岁，与弟弟<a href=\\"https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BE%99\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">苏辙</a>一同进京参加会考，苏轼中进士第2名。<br>当时主试官是<a href=\\"https://zh.wikipedia.org/wiki/%E6%AD%90%E9%99%BD%E8%84%A9\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">欧阳脩</a>，苏轼以一篇《刑赏忠厚之至论》的论文得到考官梅尧臣的青睐，并推荐给主试官欧阳修。欧阳修亦十分赞赏，原本欲拔擢为第一，但又怕该文为自己的门生<a href=\\"https://zh.wikipedia.org/wiki/%E6%9B%BE%E9%9E%8F\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">曾巩</a>所作，为了避嫌，列为第二。结果试卷拆封后才发现该文为苏轼所作，而取为第一的是<a href=\\"https://zh.wikipedia.org/wiki/%E6%9B%BE%E9%9E%8F\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">曾巩</a>，正是阴错阳差，弄巧成拙。<br>到了礼部复试时，苏轼再以《春秋对义》居曾巩之前，中乙科。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E6%B2%BB%E5%B9%B3\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">治平</a>三年</td>\\n<td>1066</td>\\n<td>父过世</td>\\n<td>父<a href=\\"https://zh.wikipedia.org/wiki/%E8%98%87%E6%B4%B5\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">苏洵</a>过世，苏轼回<a href=\\"https://zh.wikipedia.org/wiki/%E8%9C%80\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">蜀</a>守丧，英宗怜之，同意以官船载运苏轼一家。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E7%86%99%E5%AF%A7\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">熙宁</a>二年</td>\\n<td>1069</td>\\n<td></td>\\n<td>任祠部员外郎，反对<a href=\\"https://zh.wikipedia.org/wiki/%E7%8E%8B%E5%AE%89%E7%9F%B3%E8%AE%8A%E6%B3%95\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">王安石变法</a>中的一些作为，<a href=\\"https://zh.wikipedia.org/wiki/%E7%8E%8B%E5%AE%89%E7%9F%B3\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">王安石</a>于是屡次在神宗面前诋毁苏轼，<a href=\\"https://zh.wikipedia.org/wiki/%E5%8F%B8%E9%A6%AC%E5%85%89\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">司马光</a>、<a href=\\"https://zh.wikipedia.org/wiki/%E8%8C%83%E9%8E%AE\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">范镇</a>举荐苏轼作<a href=\\"https://zh.wikipedia.org/wiki/%E8%AB%AB%E5%AE%98\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">谏官</a>，王安石力反之，神宗想让苏轼写<a href=\\"https://zh.wikipedia.org/wiki/%E8%B5%B7%E5%B1%85%E6%B3%A8\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">起居注</a>，王安石向神宗进言，说苏轼在回家守丧时，乘机贩运<a href=\\"https://zh.wikipedia.org/wiki/%E8%98%87%E6%9C%A8\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">苏木</a>（一种染料），最后神宗放弃这个任命。<br>三年，因为苏轼一直反对王安石，王安石门下的<a href=\\"https://zh.wikipedia.org/wiki/%E5%BE%A1%E5%8F%B2\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">御史</a><a href=\\"https://zh.wikipedia.org/wiki/%E8%AC%9D%E6%99%AF%E6%BA%AB\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">谢景温</a>又诬陷苏轼贩卖<a href=\\"https://zh.wikipedia.org/wiki/%E7%A7%81%E7%9B%90\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">私盐</a>，<a href=\\"https://zh.wikipedia.org/wiki/%E8%8C%83%E9%8E%AE\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">范镇</a>极辩苏轼贩盐之诬，并愿意退休负责。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E7%86%99%E5%AE%81\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">熙宁</a>三年</td>\\n<td>1070</td>\\n<td></td>\\n<td>苏轼担任当年度的科举主考官，苏轼本欲拟<a href=\\"https://zh.wikipedia.org/wiki/%E4%B8%8A%E5%AE%98%E5%9D%87\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">上官均</a>为第一名（状元），因发现上官均的策论有诋毁王安石变法的情况，便改上官均为第二名。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E7%86%99%E5%AE%81\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">熙宁</a>五年</td>\\n<td>1072</td>\\n<td></td>\\n<td>苏轼因不堪<a href=\\"https://zh.wikipedia.org/wiki/%E6%96%B0%E9%BB%A8\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">新党</a>的迫害，求外职，神宗本欲予以<a href=\\"https://zh.wikipedia.org/wiki/%E7%9F%A5%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">知州</a>，但王安石只愿予之<a href=\\"https://zh.wikipedia.org/wiki/%E9%A2%8D%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">颍州</a><a href=\\"https://zh.wikipedia.org/wiki/%E9%80%9A%E5%88%A4\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">通判</a>，神宗最后折中，让苏轼担任比较好的<a href=\\"https://zh.wikipedia.org/wiki/%E6%9D%AD%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">杭州</a><a href=\\"https://zh.wikipedia.org/wiki/%E9%80%9A%E5%88%A4\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">通判</a>，三年之后升为<a href=\\"https://zh.wikipedia.org/wiki/%E7%9F%A5%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">知州</a>，连知<a href=\\"https://zh.wikipedia.org/wiki/%E5%AF%86%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">密州</a>、<a href=\\"https://zh.wikipedia.org/wiki/%E5%BE%90%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">徐州</a>、<a href=\\"https://zh.wikipedia.org/wiki/%E6%B9%96%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">湖州</a>。<br>熙宁十年四月，赴任徐州，是年七月七日，<a href=\\"https://zh.wikipedia.org/wiki/%E9%BB%84%E6%B2%B3\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">黄河</a>决口，水困<a href=\\"https://zh.wikipedia.org/wiki/%E5%BE%90%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">徐州</a>，苏轼参加救灾。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E5%85%83%E4%B8%B0_(%E5%8C%97%E5%AE%8B)\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">元丰</a>二年</td>\\n<td>1079</td>\\n<td>乌台诗案<br>被贬黄州</td>\\n<td>四十三岁时，因<a href=\\"https://zh.wikipedia.org/wiki/%E7%83%8F%E8%87%BA%E8%A9%A9%E6%A1%88\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">乌台诗案</a>入狱，几死，因为写文章向<a href=\\"https://zh.wikipedia.org/wiki/%E6%9C%9D%E5%BB%B7\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">朝廷</a>诀别，<a href=\\"https://zh.wikipedia.org/wiki/%E6%85%88%E8%81%96%E7%9A%87%E5%90%8E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">太皇太后曹氏</a>、<a href=\\"https://zh.wikipedia.org/wiki/%E7%8E%8B%E5%AE%89%E7%A4%BC\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">王安礼</a>等人出面力挽，神宗动心，苏轼终免一死，贬谪为“检校尚书水部员外郎黄州团练副使本州安置”，为其文学创作生涯的重要阶段，而神宗亦爱其才，终得以保全，翌年被贬至<a href=\\"https://zh.wikipedia.org/wiki/%E9%BB%84%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">黄州</a>（今湖北省黄冈市），在黄州“深自闭塞，扁舟革履，放浪山水之间，与渔樵杂处”[<a href=\\"https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BD%BC#cite_note-12\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">12]</a>，与<a href=\\"https://zh.wikipedia.org/wiki/%E5%BC%B5%E6%87%B7%E6%B0%91\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">张怀民</a>交游，也结交<a href=\\"https://zh.wikipedia.org/wiki/%E7%A6%AA%E9%96%80\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">禅门</a>人士，当时<a href=\\"https://zh.wikipedia.org/wiki/%E4%BD%9B%E5%8D%B0_(%E5%AE%8B%E6%9C%9D)\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">佛印</a>担任<a href=\\"https://zh.wikipedia.org/wiki/%E5%BA%90%E5%B1%B1\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">庐山</a><a href=\\"https://zh.wikipedia.org/w/index.php?title=%E5%BD%92%E5%AE%97%E5%AF%BA&amp;action=edit&amp;redlink=1\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">归宗寺</a>住持，与苏轼时有往来[<a href=\\"https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BD%BC#cite_note-13\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">13]</a>。苏轼有〈戏答佛印偈〉曰：“百千灯作一灯光，尽是<a href=\\"https://zh.wikipedia.org/wiki/%E6%81%86%E6%B2%B3%E6%B2%99\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">恒沙</a><a href=\\"https://zh.wikipedia.org/wiki/%E4%BD%9B\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">妙法王</a>，是故东坡不敢借，借君四大作禅床。”元丰七年离开黄州。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E5%85%83%E7%A5%90\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">元祐</a>元年</td>\\n<td>1086</td>\\n<td></td>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E5%AE%8B%E5%93%B2%E5%AE%97\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">宋哲宗</a>即位，<a href=\\"https://zh.wikipedia.org/wiki/%E5%AE%A3%E4%BB%81%E7%9A%87%E5%90%8E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">高太皇太后</a>垂帘听政，回朝任<a href=\\"https://zh.wikipedia.org/wiki/%E7%A4%BC%E9%83%A8\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">礼部</a><a href=\\"https://zh.wikipedia.org/wiki/%E9%83%8E%E4%B8%AD\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">郎中</a>、<a href=\\"https://zh.wikipedia.org/wiki/%E4%B8%AD%E4%B9%A6%E8%88%8D%E4%BA%BA\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">中书舍人</a>、翰林学士，元祐四年（1089年）拜<a href=\\"https://zh.wikipedia.org/wiki/%E9%BE%99%E5%9B%BE%E9%98%81\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">龙图阁</a>学士，曾出知<a href=\\"https://zh.wikipedia.org/wiki/%E6%9D%AD%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">杭州</a>、<a href=\\"https://zh.wikipedia.org/wiki/%E9%A2%8D%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">颍州</a>等，官至<a href=\\"https://zh.wikipedia.org/wiki/%E7%A6%AE%E9%83%A8%E5%B0%9A%E6%9B%B8\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">礼部尚书</a>。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E7%BB%8D%E5%9C%A3\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">绍圣</a>元年</td>\\n<td>1094</td>\\n<td>被贬惠州、儋州</td>\\n<td>被哲宗贬谪至<a href=\\"https://zh.wikipedia.org/wiki/%E6%83%A0%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">惠州</a>、<a href=\\"https://zh.wikipedia.org/wiki/%E5%84%8B%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">儋州</a>（<a href=\\"https://zh.wikipedia.org/wiki/%E6%B5%B7%E5%8D%97%E5%B3%B6\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">海南岛</a>）。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E5%85%83%E7%AC%A6\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">元符</a>三年</td>\\n<td>1100</td>\\n<td>北还</td>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E5%AE%8B%E5%BE%BD%E5%AE%97\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">宋徽宗</a>即位，<a href=\\"https://zh.wikipedia.org/wiki/%E6%AC%BD%E8%81%96%E7%9A%87%E5%90%8E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">向太后</a>垂帘听政，下诏让苏轼北还。</td>\\n</tr>\\n<tr>\\n<td><a href=\\"https://zh.wikipedia.org/wiki/%E5%BB%BA%E4%B8%AD%E9%9D%96%E5%9B%BD\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">建中靖国</a>元年</td>\\n<td>1101</td>\\n<td></td>\\n<td>夏天因冷饮过度，<a href=\\"https://zh.wikipedia.org/wiki/%E4%B8%8B%E7%97%A2\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">下痢</a>不止，又误服<a href=\\"https://zh.wikipedia.org/wiki/%E9%BB%84%E8%8A%AA\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">黄芪</a>，结果病情恶化，“齿间出血如蚯蚓者无数”，七月二十八日于<a href=\\"https://zh.wikipedia.org/wiki/%E5%B8%B8%E5%B7%9E\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">常州</a><a href=\\"https://zh.wikipedia.org/wiki/%E8%97%A4%E8%8A%B1%E6%97%A7%E9%A6%86\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">孙氏馆</a>病卒，[<a href=\\"https://zh.wikipedia.org/wiki/%E8%8B%8F%E8%BD%BC#cite_note-14\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">14]</a>享年六十四岁。由弟苏辙归葬于郏县小峨眉山。<a href=\\"https://zh.wikipedia.org/wiki/%E5%8D%97%E5%AE%8B\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">南宋</a><a href=\\"https://zh.wikipedia.org/wiki/%E5%AE%8B%E5%AD%9D%E5%AE%97\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">宋孝宗</a><a href=\\"https://zh.wikipedia.org/wiki/%E8%BF%BD%E8%B4%88\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">追赠</a>谥号“文忠”。</td>\\n</tr>\\n</tbody>\\n</table>","autoDesc":true,"bioChainData":{"outlink":[],"backlink":[],"localMap":{"nodes":[{"id":"MdNote_Public/02. SubjectAndKnowledge/06. 历史学/历史/历史杂记.md","value":{"title":"历史杂记","path":"MdNote_Public/02. SubjectAndKnowledge/06. 历史学/历史/历史杂记.md","outlink":[],"backlink":[]}}],"links":[]}}}');export{l as comp,k as data};
